mapper.select:


select *
from(
select rownum rn, z.*
from (	

select com_name, com_addr, com_bus, com_img , com_id
		from company
		where com_name Like '%'||#{mainsearch}||'%'
		or com_addr Like '%'||#{mainsearch}||'%'
		or com_bus Like '%'||#{mainsearch}||'%'

		) z
	 )
where rn between #{start} and #{end}
