mapper.select:

		select *
		from 
    		(
    		 select rownum rn, z.*
    		 from ( 
    		 		select * 
			 		from announce
			 		where ANNO_C_REGDATE < sysdate
			 		and com_id = 'tlstprp5184'
			       ) z
    		 )
		where rn between #{start} and #{end}

		select count(*)
		from bookmark 
		where com_id =  #{com_id}
		and user_id = #{user_id}
   		and MRK_RES_CODE = #{mrk_res_code}

mapper.delete:
delete 
from bookmark 
where com_id = #{com_id} 
and user_id = #{user_id}
and mrk_res_code = ${mrk_res_code}

mapper.select:
select bookmark.*
from bookmark
where com_id=#{com_id}
and user_id=#{user_id}
and mrk_res_code=#{mrk_res_code}
and it_bookmark='1'
