mapper.insert:
insert into post(
	post_code
	, brd_code
	, post_title
	, post_ctx
	, post_regdate
	, user_id
) values (
	post_seq.NEXTVAL
	, #{brd_code}
	, #{post_title}
	, #{post_ctx}
	, sysdate
	, #{user_id}
)

mapper.update:
update post
set
	post_title = #{post_title}
	, post_ctx = #{post_ctx}
	, user_id = #{user_id}
where 
	post_code = #{post_code}

mapper.insert:
insert into post(
	post_code
	, brd_code
	, post_title
	, post_ctx
	, post_regdate
	, user_id
	, ref
	, ref_step
	, ref_lev
) values (
	post_seq.NEXTVAL
	, #{brd_code}
	, #{post_title}
	, #{post_ctx}
	, #{post_regdate}
	, #{user_id}
	, #{ref}
	, #{ref_step}
	, #{ref_lev}
)
order by ref_step asc
